  <path stroke-linecap="round" stroke-linejoin="round" d="M19.5 19.5l-15-15m0 0v11.25m0-11.25h11.25" />
</svg>

          </a></li>
      </ul>
      
      <hr class="h-[2px]">
      <div>
      <h3>Continuum Glow</h3>
      <p class="opacity-[70%]">"Achieve innovation,<br> fueled by AI"</p>
     </div>
    </div>
  </header>
</template>

<script>
import { gsap } from 'gsap';

export default {
  data() {
    return {
      menuOpen: false,
      menuTimeline: null,
    };
  },
  mounted() {
    gsap.set(this.$refs.menu, { x: '100%' });
    this.menuTimeline = gsap.timeline({ paused: true })
      .to(this.$refs.menu, { x: '0%', duration: 0.5, ease: 'power2.out' });

    // Add event listener for scroll
    window.addEventListener('scroll', this.handleScroll);
  },
  beforeDestroy() {
    window.removeEventListener('scroll', this.handleScroll);
  },
  watch: {
    menuOpen(newValue) {
      this.animateIcon(newValue);
      if (newValue) {
        this.menuTimeline.play();
      } else {
        this.menuTimeline.reverse();
      }
    },
  },
  methods: {
    toggleMenu() {
      this.menuOpen = !this.menuOpen;
    },
    animateIcon(open) {
      if (open) {
        gsap.to(this.$refs.line1, {attr: {d: 'M6 18L18 6'}, duration: 0.2});
        gsap.to(this.$refs.line2, {opacity: 0, duration: 0.1});
        gsap.to(this.$refs.line3, {attr: {d: 'M6 6l12 12'}, duration: 0.2});
      } else {
        gsap.to(this.$refs.line1, {attr: {d: 'M3.75 6.75h16.5'}, duration: 0.2});
        gsap.to(this.$refs.line2, {opacity: 1, duration: 0.1});
        gsap.to(this.$refs.line3, {attr: {d: 'M3.75 17.25h16.5'}, duration: 0.2});
      }
    },
    handleScroll() {
      if (window.scrollY > 30) {
        this.$refs.header.classList.add('scrolled');
      } else {
        this.$refs.header.classList.remove('scrolled');
      }
    }
  },
};
</script>

