.scrolled {
  background: rgba(147, 147, 147, 0.497);
  box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.10);
  backdrop-filter: blur(20.5px);
  -webkit-backdrop-filter: blur(20.5px);
  border-radius: 0px;
}
hr {
    height: 2px;
    width: 100%;
    border: none;
    background-color: white;
